}

const text = isCallback ? (u?.callback_query?.data || "") : (m?.text || "");
const messageId = m?.message_id ? String(m.message_id) : null;
const callbackQueryId = isCallback ? (u?.callback_query?.id || null) : null;
const isPrivate = (chat?.type === 'private');

// session_id simple por d√≠a (trazabilidad)
