// Objetivo: tomar el JSON que devolvi√≥ la IA y extraer literalmente la intenci√≥n,
// sin "reconvertir" nada. Si no hay JSON v√°lido, devolvemos 'fuera_de_contexto'.

function asObject(x) {
  if (!x) return null;
  if (typeof x === 'object') return x;
  if (typeof x === 'string') {
    // si viene texto con JSON embebido, toma el primer {...}
    const m = x.match(/\{[\s\S]*\}/);
    const s = m ? m[0] : x;
    try { return JSON.parse(s); } catch { return null; }
  }
  return null;
}

// La salida del AI Agent puede venir en distintos campos seg√∫n el nodo/versi√≥n
const candidates = [
  $json.response,   // /api/generate (HTTP)
  $json.text,       // AI Agent / Chat Model
  $json.output,     // Algunas integraciones de LangChain
  $json.data,
  $json.message,
  $json.result
];

let intent = null;
for (const c of candidates) {
  const obj = asObject(c);
  if (!obj) continue;
  const val =
    obj['intenci√≥n'] ??
    obj['intencion'] ??
    obj['intention'] ??
    obj['intent'] ??
    obj['categoria'];
  if (typeof val === 'string' && val.trim()) {
    intent = val.trim().toLowerCase();
    break;
  }
}

// √öltimo recurso: buscar la clave en el JSON stringificado completo del nodo
if (!intent) {
  const raw = JSON.stringify($json);
  const m = raw.match(/"intenci(?:√≥|o)n"\s*:\s*"([^"]+)"/i);
  if (m) intent = m[1].trim().toLowerCase();
}

return [{ json: { intencion: intent || 'fuera_de_contexto' } }];

