// Parse Budget Selection -> {route, code, min, max}
const raw = $node["Normalize"].json.text || ""; // ej: budget:r:200-400, budget:other, budget:change_city
const m = /^budget:(r:(\d+)-(\d+)|other|change_city|home)$/.exec(raw);
if (!m) {
  return [{ json: { route: "invalid", raw } }];
}
const code = m[1];
let route = "ok";
let min = null, max = null;
if (code === "other") route = "other";
else if (code === "change_city") route = "change_city";
else if (code === "home") route = "home";
else {
  min = Number(m[2]);
  max = Number(m[3]);
}
return [{ json: { route, code, min, max } }];

const txt = $node["Normalize"].json.text || "";
const out = { precio_min: null, precio_max: null, accion: null };
if (txt.startsWith("presu_")) {
  const code = txt.slice("presu_".length);
  if (code === "cambiar_ciudad") out.accion = "cambiar_ciudad";
  else if (code === "mas_600") { out.precio_min = 600_000_000; out.precio_max = null; }
  else {
    const [lo, hi] = code.split("_").map(n => parseInt(n, 10));
    out.precio_min = lo * 1_000_000;
    out.precio_max = hi * 1_000_000;
  }
}
return [{ json: out }];

